api_generator.content:
(function() {
  var ApiGenerator;

  ApiGenerator = function(root, requester) {
    var api, requestOptions;
    requestOptions = function(type, data) {
      return {
        type: type,
        data: JSON.stringify(data)
      };
    };
    api = function(path, options) {
      var url;
      if (path.match(/^http/)) {
        url = path;
      } else {
        url = "" + (root()) + "/" + path;
      }
      return requester(url, options);
    };
    return {
      api: api,
      get: function(path, data) {
        return api(path, {
          data: data
        });
      },
      put: function(path, data) {
        return api(path, requestOptions("PUT", data));
      },
      post: function(path, data) {
        return api(path, requestOptions("POST", data));
      },
      patch: function(path, data) {
        return api(path, requestOptions("PATCH", data));
      },
      DELETE: function(path, data) {
        return api(path, requestOptions("DELETE", data));
      }
    };
  };

  module.exports = ApiGenerator;

}).call(this);

