(function() {
  var Github, Observable, Repository, defaults, extend, _ref;

  Repository = require("./repository");

  Observable = require("observable");

  _ref = require("./lib/util"), defaults = _ref.defaults, extend = _ref.extend;

  Github = function(tokenPromise) {
    var api, lastRequest, token;
    token = null;
    if (tokenPromise != null) {
      tokenPromise.then(function(tokenValue) {
        return token = tokenValue;
      });
    }
    lastRequest = Observable();
    api = function(path, options) {
      var url;
      if (options == null) {
        options = {};
      }
      if (path.match(/^http/)) {
        url = path;
      } else {
        url = "https://api.github.com/" + path;
      }
      options.headers || (options.headers = {});
      if (token) {
        options.headers["Authorization"] = "token " + token;
      }
      options = extend({
        url: url,
        type: "GET",
        dataType: 'json',
        contentType: "application/json; charset=utf-8"
