(function() {
  var Model;

  Model = require("../main");

  describe('Model', function() {
    var Person;
    Person = function(I) {
      var person;
      person = Model(I);
      person.attrAccessor('firstName', 'lastName', 'suffix');
      person.fullName = function() {
        return "" + (this.firstName()) + " " + (this.lastName()) + " " + (this.suffix());
      };
      return person;
    };
    describe("#attrObservable", function() {
      it('should allow for observing of attributes', function() {
        var model;
        model = Model({
          name: "Duder"
        });
        model.attrObservable("name");
        model.name("Dudeman");
        return assert.equal(model.name(), "Dudeman");
      });
      return it('should bind properties to observable attributes', function() {
        var model;
        model = Model({
          name: "Duder"
        });
        model.attrObservable("name");
        model.name("Dudeman");
        assert.equal(model.name(), "Dudeman");
        return assert.equal(model.name(), model.I.name);
      });
    });
    describe("#attrModel", function() {
      it("should be a model instance", function() {
        var model;
        model = Model({
          person: {
            firstName: "Duder",
            lastName: "Mannington",
            suffix: "Jr."
          }
        });
        model.attrModel("person", Person);
        return assert.equal(model.person().fullName(), "Duder Mannington Jr.");
      });
      it("should allow setting the associated model", function() {
        var model, otherPerson;
        model = Model({
          person: {
            firstName: "Duder",
            lastName: "Mannington",
            suffix: "Jr."
          }
        });
        model.attrModel("person", Person);
        otherPerson = Person({
          firstName: "Mr.",
          lastName: "Man"
        });
        model.person(otherPerson);
        return assert.equal(model.person().firstName(), "Mr.");
      });
      return it("shouldn't update the instance properties after it's been replaced", function() {
        var duder, model, otherPerson;
        model = Model({
          person: {
            firstName: "Duder",
            lastName: "Mannington",
            suffix: "Jr."
          }
        });
        model.attrModel("person", Person);
        duder = model.person();
        otherPerson = Person({
          firstName: "Mr.",
          lastName: "Man"
        });
        model.person(otherPerson);
        duder.firstName("Joe");
        assert.equal(duder.I.firstName, "Joe");
        return assert.equal(model.I.person.firstName, "Mr.");
      });
    });
    describe("#attrModels", function() {
      it("should have an array of model instances", function() {
        var model;
        model = Model({
          people: [
            {
              firstName: "Duder",
              lastName: "Mannington",
              suffix: "Jr."
            }, {
              firstName: "Mr.",
              lastName: "Mannington",
              suffix: "Sr."
            }
          ]
        });
        model.attrModels("people", Person);
        return assert.equal(model.people()[0].fullName(), "Duder Mannington Jr.");
      });
      it("should track pushes", function() {
        var model;
        model = Model({
          people: [
            {
              firstName: "Duder",
              lastName: "Mannington",
              suffix: "Jr."
            }, {
              firstName: "Mr.",
              lastName: "Mannington",
              suffix: "Sr."
            }
          ]
        });
        model.attrModels("people", Person);
        model.people.push(Person({
          firstName: "JoJo",
          lastName: "Loco"
        }));
        assert.equal(model.people().length, 3);
        return assert.equal(model.I.people.length, 3);
      });
      return it("should track pops", function() {
        var model;
        model = Model({
          people: [
            {
              firstName: "Duder",
              lastName: "Mannington",
              suffix: "Jr."
            }, {
              firstName: "Mr.",
              lastName: "Mannington",
              suffix: "Sr."
            }
          ]
        });
        model.attrModels("people", Person);
        model.people.pop();
        assert.equal(model.people().length, 1);
        return assert.equal(model.I.people.length, 1);
      });
    });
    describe("#toJSON", function() {
      return it("should return an object appropriate for JSON serialization", function() {
        var model;
        model = Model({
          test: true
        });
        return assert(model.toJSON().test);
      });
    });
    return describe("#observeAll", function() {
      it("should observe all attributes of a simple model");
      (function() {
        var model;
        model = Model({
          test: true,
          yolo: "4life"
        });
        model.observeAll();
        assert(model.test());
        return assert.equal(model.yolo(), "4life");
      });
      return it("should camel case underscored names");
    });
  });

}).call(this);

