(function() {
  global.Observable = require("../main");

  describe('Observable', function() {
    it('should create an observable for an object', function() {
      var n, observable;
      n = 5;
      observable = Observable(n);
      return assert.equal(observable(), n);
    });
    it('should fire events when setting', function() {
      var observable, string;
      string = "yolo";
      observable = Observable(string);
      observable.observe(function(newValue) {
        return assert.equal(newValue, "4life");
      });
      return observable("4life");
    });
    it('should be idempotent', function() {
      var o;
      o = Observable(5);
      return assert.equal(o, Observable(o));
    });
    describe("#each", function() {
      it("should be invoked once if there is an observable", function() {
        var called, o;
        o = Observable(5);
        called = 0;
        o.each(function(value) {
          called += 1;
          return assert.equal(value, 5);
        });
        return assert.equal(called, 1);
      });
      return it("should not be invoked if observable is null", function() {
        var called, o;
        o = Observable(null);
        called = 0;
        o.each(function(value) {
          return called += 1;
        });
        return assert.equal(called, 0);
      });
    });
    it("should allow for stopping observation", function() {
      var called, fn, observable;
      observable = Observable("string");
      called = 0;
      fn = function(newValue) {
        called += 1;
        return assert.equal(newValue, "4life");
      };
      observable.observe(fn);
      observable("4life");
      observable.stopObserving(fn);
      observable("wat");
      return assert.equal(called, 1);
    });
    it("should increment", function() {
      var observable;
      observable = Observable(1);
      observable.increment(5);
      return assert.equal(observable(), 6);
    });
    it("should decremnet", function() {
      var observable;
      observable = Observable(1);
      observable.decrement(5);
      return assert.equal(observable(), -4);
    });
    it("should toggle", function() {
      var observable;
      observable = Observable(false);
      observable.toggle();
      assert.equal(observable(), true);
      observable.toggle();
      return assert.equal(observable(), false);
    });
    return it("should trigger when toggling", function(done) {
      var observable;
      observable = Observable(true);
      observable.observe(function(v) {
        assert.equal(v, false);
        return done();
      });
      return observable.toggle();
    });
  });

  describe("Observable Array", function() {
    it("should proxy array methods", function() {
      var o;
      o = Observable([5]);
      return o.map(function(n) {
        return assert.equal(n, 5);
      });
    });
    it("should notify on mutation methods", function(done) {
      var o;
      o = Observable([]);
      o.observe(function(newValue) {
        return assert.equal(newValue[0], 1);
      });
      o.push(1);
      return done();
    });
    it("should have an each method", function() {
      var o;
      o = Observable([]);
      return assert(o.each);
    });
    it("#get", function() {
      var o;
      o = Observable([0, 1, 2, 3]);
      return assert.equal(o.get(2), 2);
    });
    it("#first", function() {
      var o;
      o = Observable([0, 1, 2, 3]);
      return assert.equal(o.first(), 0);
    });
    it("#last", function() {
      var o;
      o = Observable([0, 1, 2, 3]);
      return assert.equal(o.last(), 3);
    });
    it("#remove", function(done) {
      var o;
      o = Observable([0, 1, 2, 3]);
      o.observe(function(newValue) {
        assert.equal(newValue.length, 3);
        return setTimeout(function() {
          return done();
        }, 0);
      });
      return assert.equal(o.remove(2), 2);
    });
    return it("should proxy the length property");
  });

  describe("Observable functions", function() {
    it("should compute dependencies", function(done) {
      var firstName, lastName, o;
      firstName = Observable("Duder");
      lastName = Observable("Man");
      o = Observable(function() {
        return "" + (firstName()) + " " + (lastName());
      });
      o.observe(function(newValue) {
        assert.equal(newValue, "Duder Bro");
        return done();
      });
      return lastName("Bro");
    });
    it("should allow double nesting", function(done) {
      var bottom, middle, top;
      bottom = Observable("rad");
      middle = Observable(function() {
        return bottom();
      });
      top = Observable(function() {
        return middle();
      });
      top.observe(function(newValue) {
        assert.equal(newValue, "wat");
        assert.equal(top(), newValue);
        assert.equal(middle(), newValue);
        return done();
      });
      return bottom("wat");
    });
    it("should work with dynamic dependencies", function() {
      var dynamicObservable, observableArray;
      observableArray = Observable([]);
      dynamicObservable = Observable(function() {
        return observableArray.filter(function(item) {
          return item.age() > 3;
        });
      });
      assert.equal(dynamicObservable().length, 0);
      observableArray.push({
        age: Observable(1)
      });
      observableArray()[0].age(5);
      return assert.equal(dynamicObservable().length, 1);
    });
    it("should work with context", function() {
      var model;
      model = {
        a: Observable("Hello"),
        b: Observable("there")
      };
      model.c = Observable(function() {
        return "" + (this.a()) + " " + (this.b());
      }, model);
      assert.equal(model.c(), "Hello there");
      model.b("world");
      return assert.equal(model.c(), "Hello world");
    });
    it("should be ok even if the function throws an exception", function() {
      assert.throws(function() {
        var t;
        return t = Observable(function() {
          throw "wat";
        });
      });
      return assert.equal(global.OBSERVABLE_ROOT_HACK.length, 0);
    });
    it("should have an each method", function() {
      var o;
      o = Observable(function() {});
      return assert(o.each);
    });
    it("should not invoke when returning undefined", function() {
      var o;
      o = Observable(function() {});
      return o.each(function() {
        return assert(false);
      });
    });
    it("should invoke when returning any defined value", function(done) {
      var o;
      o = Observable(function() {
        return 5;
      });
      return o.each(function(n) {
        assert.equal(n, 5);
        return done();
      });
    });
    it("should work on an array dependency", function() {
      var last, o, oA;
      oA = Observable([1, 2, 3]);
      o = Observable(function() {
        return oA()[0];
      });
      last = Observable(function() {
        return oA()[oA().length - 1];
      });
      assert.equal(o(), 1);
      oA.unshift(0);
      assert.equal(o(), 0);
      oA.push(4);
      return assert.equal(last(), 4, "Last should be 4");
    });
    it("should work with multiple dependencies", function() {
      var checked, first, letter, second;
      letter = Observable("A");
      checked = function() {
        var l;
        l = letter();
        return this.name().indexOf(l) === 0;
      };
      first = {
        name: Observable("Andrew")
      };
      first.checked = Observable(checked, first);
      second = {
        name: Observable("Benjamin")
      };
      second.checked = Observable(checked, second);
      assert.equal(first.checked(), true);
      assert.equal(second.checked(), false);
      assert.equal(letter.listeners.length, 2);
      letter("B");
      assert.equal(first.checked(), false);
      return assert.equal(second.checked(), true);
    });
    it("should work with nested observable construction", function() {
      var gen, o;
      gen = Observable(function() {
        return Observable("Duder");
      });
      o = gen();
      assert.equal(o(), "Duder");
      o("wat");
      return assert.equal(o(), "wat");
    });
    describe("Scoping", function() {
      return it("should be scoped to optional context", function(done) {
        var model;
        model = {
          firstName: Observable("Duder"),
          lastName: Observable("Man")
        };
        model.name = Observable(function() {
          return "" + (this.firstName()) + " " + (this.lastName());
        }, model);
        model.name.observe(function(newValue) {
          assert.equal(newValue, "Duder Bro");
          return done();
        });
        return model.lastName("Bro");
      });
    });
    return describe("concat", function() {
      it("should return an observable array that changes based on changes in inputs", function() {
        var item, letters, nullable, numbers, observableArray;
        numbers = Observable([1, 2, 3]);
        letters = Observable(["a", "b", "c"]);
        item = Observable({});
        nullable = Observable(null);
        observableArray = Observable.concat(numbers, "literal", letters, item, nullable);
        assert.equal(observableArray().length, 3 + 1 + 3 + 1);
        assert.equal(observableArray()[0], 1);
        assert.equal(observableArray()[3], "literal");
        assert.equal(observableArray()[4], "a");
        assert.equal(observableArray()[7], item());
        numbers.push(4);
        assert.equal(observableArray().length, 9);
        nullable("cool");
        return assert.equal(observableArray().length, 10);
      });
      it("should work with observable functions that return arrays", function() {
        var computedArray, item, observableArray;
        item = Observable("wat");
        computedArray = Observable(function() {
          return [item()];
        });
        observableArray = Observable.concat(computedArray, computedArray);
        assert.equal(observableArray().length, 2);
        assert.equal(observableArray()[1], "wat");
        item("yolo");
        return assert.equal(observableArray()[1], "yolo");
      });
      it("should have a push method", function() {
        var observable, observableArray;
        observableArray = Observable.concat();
        observable = Observable("hey");
        observableArray.push(observable);
        assert.equal(observableArray()[0], "hey");
        observable("wat");
        assert.equal(observableArray()[0], "wat");
        observableArray.push("cool");
        observableArray.push("radical");
        return assert.equal(observableArray().length, 3);
      });
      it("should be observable", function(done) {
        var observableArray;
        observableArray = Observable.concat();
        observableArray.observe(function(items) {
          assert.equal(items.length, 3);
          return done();
        });
        return observableArray.push(["A", "B", "C"]);
      });
      return it("should have an each method", function() {
        var n, observableArray;
        observableArray = Observable.concat(["A", "B", "C"]);
        n = 0;
        observableArray.each(function() {
          return n += 1;
        });
        return assert.equal(n, 3);
      });
    });
  });

}).call(this);
